import React from "react";
import './GameComponent.css';
import recycle from './recycle.png';


var correctItems = 0;
var incorrectItems = 0;
var data = ["papercup","T-shirt","glass","iPhone","battery","cardboard box","plastic box","banana peel","books","water bottle", "diapers","paper towels", "pizza box", "tires", "yogurt cups"]
var indicators = [1,1,1,0,0,1,0,1,1,1,0,0,0,0,0];
var index = 0;
var sanity;
var gamescore = 0;

class GameComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      score: 0
    };  
  }

  onSubmit = () => {
    this.clearInterval();
    this.props.setGamescore(this.state.score);
    if (this.props.isLoggedIn) {
      this.props.sendGamescore(this.props.email, this.state.score);
    }
    this.props.setGameover(true);
    return this.props.history.push("/");
  }

  countDown = () => {
    if (index >= 15) this.onSubmit();
    this.setState(() => ({
      score: gamescore
    }))
  }

  componentDidMount() {

    this.countdown = setInterval(this.countDown, 1000);

    sanity = this.props.sanity;

    var addEvent = (function () {
      if (document.addEventListener) {
        return function (el, type, fn) {
          if (el && el.nodeName || el === window) {
             el.addEventListener(type, fn, false);
          } else if (el && el.length) {
            for (var i = 0; i < el.length; i++) {
              addEvent(el[i], type, fn);
            }
          }
        };
      } else {
        return function (el, type, fn) {
          if (el && el.nodeName || el === window) {
              el.attachEvent('on' + type, function () { return fn.call(el, window.event); });
          } else if (el && el.length) {
            for (var i = 0; i < el.length; i++) {
                addEvent(el[i], type, fn);
            }
          }
        };
      }
    })();

    (function () {

      var pre = document.createElement('pre');
      pre.id = "view-source";

      addEvent(window, 'click', function (event) {
        if (event.target.hash == '#view-source') {
          if (!document.getElementById('view-source')) {
            pre.innerHTML = ('<!DOCTYPE html>\n<html>\n' + document.documentElement.innerHTML + '\n</html>').replace(/[<>]/g, function (m) { return {'<':'&lt;','>':'&gt;'}[m]});
            document.body.appendChild(pre);
          }
          document.body.className = 'view-source';

          var sourceTimer = setInterval(function () {
            if (window.location.hash != '#view-source') {
              clearInterval(sourceTimer);
              document.body.className = '';
            }
          }, 200);
        }
      });

    })();


    var eat = ['', '', '', ''];
    var yum = document.createElement('p');
    var msie = /*@cc_on!@*/0;
    yum.style.opacity = 1;

    var links = document.querySelectorAll('li > a'), el = null;
    for (var i = 0; i < links.length; i++) {
      el = links[i];

      el.setAttribute('draggable', 'true');

      addEvent(el, 'dragstart', function (e) {
        e.dataTransfer.effectAllowed = 'copy'; // only dropEffect='copy' will be dropable
        e.dataTransfer.setData('Text', this.id); // required otherwise doesn't work
      });
    }

      var bin = document.querySelector('#bin');
      var bin2 = document.querySelector('#bin2');

      addEvent(bin, 'dragover', function (e) {
          if (e.preventDefault) e.preventDefault(); // allows us to drop
          this.className = 'over';
          e.dataTransfer.dropEffect = 'copy';
          return false;
      });

      addEvent(bin2, 'dragover', function (e) {
        if (e.preventDefault) e.preventDefault(); // allows us to drop
        this.className = 'over';
        e.dataTransfer.dropEffect = 'copy';
        return false;
    });

      // to get IE to work
      addEvent(bin, 'dragenter', function (e) {
          this.className = 'over';
          return false;
      });

      // to get IE to work
      addEvent(bin2, 'dragenter', function (e) {
        this.className = 'over';
        return false;
    });

      addEvent(bin, 'dragleave', function () {
          this.className = '';
      });

      addEvent(bin2, 'dragleave', function () {
        this.className = '';
    });

      addEvent(bin, 'drop', function (e) {

          if (e.stopPropagation) e.stopPropagation(); // stops the browser from redirecting...why???

          var el = document.getElementById(e.dataTransfer.getData('Text'));

          el.parentNode.removeChild(el);
          if(indicators[index]==1) {
              correctItems = correctItems + 1;
              if (sanity) {
                gamescore++;
              }
          } else {
            if (!sanity) {
              gamescore++;
            }
          }
          index++;

          // stupid nom text + fade effect
          bin2.className = '';
          yum.innerHTML = eat[parseInt(Math.random() * eat.length)];

          var y = yum.cloneNode(true);
          bin.appendChild(y);
          setTimeout(function () {
              var t = setInterval(function () {
                  if (y.style.opacity <= 0) {
                      if (msie) { // don't bother with the animation
                          y.style.display = 'none';
                      }
                      clearInterval(t);
                  } else {
                      y.style.opacity -= 0.1;
                  }
              }, 50);
          }, 250);

          return false;
      })

      addEvent(bin2, 'drop', function (e) {
        if (e.stopPropagation) e.stopPropagation(); // stops the browser from redirecting...why???

        var el = document.getElementById(e.dataTransfer.getData('Text'));

        el.parentNode.removeChild(el);
          if(indicators[index]==0) {
              correctItems= correctItems + 1;
              if (sanity) {
                gamescore++;
              }
      } else {
        if (!sanity) {
          gamescore++;
        }
      }
          index++;
