        if(data == undefined){

        }else {
            return (
                <ul>
                    {data.map((item,index) =>
                        <li key={index}>
                            {
                                <a draggable="true" href="" id="one">{item}</a>
                            }
                        </li>
                    )}
                </ul>
            );
        }
    };


render() {
    return (
      <div class="holder">
          {this.props.sanity && <h1>Save the Earth!</h1>}
          {!this.props.sanity && <h1>Destroy the World!</h1>}
          {this.props.sanity && <h2>Score: {this.state.score}</h2>}
          {!this.props.sanity && <h2>Score: {this.state.score}</h2>}
          <div className="game-div">
          <div id="bin">
              <img alt="" className="recycle-img" src={recycle} />
          </div>
          <ul>
              {this.renderList(data)}
          </ul>
          <div id="bin2">
              <img alt="" src="data:image/png;base64," />
          </div>
          </div>
      </div>
    );
  }
}

export default GameComponent;
