import React from 'react';
import { Link } from 'react-router-dom';
import recycle from './recycle.png';
import './HomeComponent.css';

class HomeComponent extends React.Component {  

  constructor(props) {
    super(props);

    this.state = {
      score: 0,
      username: "",
      password: ""
    };
  }


  onSubmitQuestions = () => {
    if (this.state.score < 2) {
      // set sanity to "good"
      return this.props.history.push('/game');
    } else {
      // set sanity to "evil"
      return this.props.history.push('/game');
    }
  }

  onLogin = () => {
    // call function on backend to login
    // if successful, render components and set variables as needed   
  }

  onCreateAccount = () => {
    // call function on backend to create account
    // if successful, render components and set variables as needed
    // automatic login
  }

  componentDidMount () {
    const script = document.createElement("script");

    script.type = 'text/javascript';
    script.async = true;
    script.innerHTML = "var questionWindow = document.getElementById('questions'); window.onclick = function(event) { if (event.target == questionWindow) { questionWindow.style.display = \"none\"; } }"

    document.body.appendChild(script);
  }



  render() {
    return (
      <div>
        <header className="header">
          <img src={recycle} alt="Recycle Game" height="42" width="42"></img>
          <button className="header-button">Login</button>
        </header>
        <h1>Recycle Game</h1>
        <button onclick="document.getElementById('questions').style.display='block'">Play Game</button>



        <div id="questions" className="questionBlock">

          <form className="pop-up" onSubmit="onSubmitQuestions">

            <div>
              <label for="q1"><b>Username</b></label>
              <input type="text" placeholder="Enter Username" name="uname" required></input>

              <label for="q2"><b>Password</b></label>
              <input type="password" placeholder="Enter Password" name="psw" required></input>

              <label for="q2"><b>Password</b></label>
              <input type="password" placeholder="Enter Password" name="psw" required></input>

              <button type="submit">Play Game</button>
            </div>

          </form>
        </div>

      </div>
    );
  }
}

export default HomeComponent;
